import React, { Component } from 'react';
import Container from './Container';
import './App.css';
import Footer from './Footer.js';
import { getAllStudents } from './client';
import { 
  Table, Avatar, Spin, Icon, Modal
} from 'antd';


const getIndicatorIcon = () =>  <Icon type="loading" style={{ fontSize: 24 }} spin />;

class App extends Component{

  state = {
    students: [],
    isFetching: false,
    isAddStudentModalVisible:false
  }
  componentDidMount(){
    this.fetchStudents();
  }

  openAddStudentModal = ()=> this.setState({isAddStudentModalVisible: true});

  closeAddStudentModal = ()=> this.setState({isAddStudentModalVisible: false})

  fetchStudents = () => {
    this.setState({
      isFetching:true
    });
    getAllStudents()
    .then(res => res.json()
    .then(students => {
      console.log(students);
      this.setState({
        students,
        isFetching:false
      })
    }));
  }

  render(){


    const { students , isFetching } = this.state;


    if(isFetching){
      return (
        <Container>
          <Spin indicator={getIndicatorIcon}/>
        </Container>
      );
    }



   if(students && students.length ){

    const columns = [
      {
        title: '',
        key:'avatar',
        //touse custom component, use render
        render: (text ,student) =>(
          <Avatar size ='large'>
            {`${student.firstName.charAt(0).toUpperCase()}${student.lastName.charAt(0).toUpperCase()}`}
          </Avatar>
        )
      },
      {
        title: 'Student ID',
        dataIndex: 'studentId',
        key:'studentId'
      },
      {
        title: 'First Name',
        dataIndex: 'firstName',
        key:'firstName'
      },
      {
        title: 'Last Name',
        dataIndex: 'lastName',
        key:'lastName'
      },
      {
        title: 'Email',
        dataIndex: 'email',
        key:'email'
      },
      {
        title: 'Gender',
        dataIndex: 'gender',
        key:'gender'
      }

    ];

    return (
      <Container>
        <Table 
          dataSource = {students} 
          columns = {columns} 
          pagination = {false}
          rowKey = 'studentId' />
        <Footer numberOfStudents = {students.length}></Footer>
      </Container>
    )
   }

    return <h1>No Students Found</h1>
  }


}

export default App;
