import React, { Component } from 'react';
import Container from './Container';
import './App.css';
import { getAllStudents } from './client';
import { 
  Table, Avatar
} from 'antd';


class App extends Component{

  state = {
    students: []
  }
  componentDidMount(){
    this.fetchStudents();
  }

  fetchStudents = () => {
    getAllStudents()
    .then(res => res.json()
    .then(students => {
      console.log(students);
      this.setState({
        students
      })
    }));
  }

  render(){

    const { students } = this.state;

   if(students && students.length ){

    const columns = [
      {
        title: '',
        key:'avatar',
        //touse custom component, use render
        render: (text ,student) =>(
          <Avatar size ='large'>
            {`${student.firstName.charAt(0).toUpperCase()}${student.lastName.charAt(0).toUpperCase()}`}
          </Avatar>
        )
      },
      {
        title: 'Student ID',
        dataIndex: 'studentId',
        key:'studentId'
      },
      {
        title: 'First Name',
        dataIndex: 'firstName',
        key:'firstName'
      },
      {
        title: 'Last Name',
        dataIndex: 'lastName',
        key:'lastName'
      }

    ];

    return (
      <Container>
        <Table 
          dataSource = {students} 
          columns = {columns} 
          pagination = {false}
          rowKey = 'studentId' />
      </Container>
    )
   }

    return <h1>No Students Found</h1>
  }


}

export default App;
